import { connect } from 'react-redux'
import React, { Component } from 'react'
import R from 'ramda'
import getCoin from 'selectors'
import { Link } from 'react-router'
import Menu from '../menu'
import { signout } from 'actions'

class Balance extends Component {
componentDidMount(){
    window.scrollTo(0, 0)
}
    render() {
        
        return (
            <div>
                <div>
                    <div className="personal-header">
                        <div className="header-overlay">
                           <Menu />
                            <div className="price-header-content">
                                <h2 className="price-header-title">Welcome to your profile</h2>
                                <p className="price-header-text"></p>
                            </div>{/* header-content */}
                        </div>{/* header-overlay */}
                    </div>{/* personal-header */}
                    <div className="personal-container">
                        <div className="wrapper">
                            <h2 className="wallet-block-title">Balance</h2>
                            <div className="wallet-block">
                                <div className="wallet-et">
                                    <span className="wallet-et-header">ETH balance</span>
                                    <b>Balance:</b>
                                    <span className="wallet-et-sum">0 eth</span>
                                    <b>Your adress:</b>
                                    <span className="wallet-et-adr"><p className='et-to'>0x0074709077B8AE5a245E4ED161C971Dc4c3C8E2B</p></span>
                                    <div className="wallet-rub-row">
                                    <div className="wallet-rub-column">
                                            <span>Вывести депозит</span>
                                    <button className="wallet-rub-btn orange-btn">Вывод</button></div>
                                </div>{/* wallet-rub-column */}
                                </div>{/* wallet-et */}
                                <div className="wallet-rub">
                                    <span className="wallet-rub-header">RUR &amp; USD</span>
                                    <b>Balance:</b>
                                    <div className="wallet-rub-sum-container">
                                        <span>RUR: 1,480,790,01 р</span>
                                        <span className="wallet-rub-line" />
                                        <span>USD: 73 $</span>
                                    </div>{/* wallet-rub-sum-container */}
                                    {(!this.props.hideButton) ? <b className="wallet-rub-title">Операции с балансом</b> : <b className="wallet-rub-title">EEEEE</b> }
                                    
                                    <div className="wallet-rub-row">
                                        <div className="wallet-rub-column">
                                            <span>RUR</span>
                                            <button className="wallet-rub-btn orange-btn">Пополнить</button>
                                            <button className="wallet-rub-btn orange-btn">Вывести</button>
                                        </div>{/* wallet-rub-column */}
                                        <div className="wallet-rub-column">
                                            <span>USD</span>
                                            <button className="wallet-rub-btn green-btn">Пополнить</button>
                                            <button className="wallet-rub-btn green-btn">Вывести</button>
                                        </div>{/* wallet-rub-column */}
                                    </div>{/* wallet-rub-row */}
                                </div>{/* wallet-rub */}
                            </div>{/* wallet-block */}
                        </div>{/* wrapper */}
                    </div>{/* personal-container */}
                    <div className="personal-info">
                        <div className="wrapper">
                           
 
                        </div>{/* wrapper */}
                    </div>{/* balance-info */}
                    <div className="footer">
                        <div className="wrapper">
                            <div className="footer-container">
                                <div className="footer-menu">
                                    <span className="footer-menu-title">Help &amp; Support</span>
                                    <button className="footer-menu-btn">read more</button>
                                </div>{/* footer-menu */}
                                <div className="footer-menu">
                                    <span className="footer-menu-title">FAQ</span>
                                    <button className="footer-menu-btn">read more</button>
                                </div>{/* footer-menu */}
                            </div>{/* footer-container */}
                            <p className="footer-text">Packages for mobile, IoT, front end, back end, robotics… everything you need to start building amazing things.</p>
                        </div>{/* wrapper */}
                        <p className="footer-copyright">© 2012 – 2018, www.sitename.by</p>
                    </div>{/* footer */}
                </div>

            </div>
        )
    }
}

//Раскомментировать эту константу, когда уберется перезагрузка страниц
const mapStateToProps = state => ({
    hideButton: state.auth.hideButton
})
///

const mapDispatchToProps = {
    signout
}


export default connect(mapStateToProps,mapDispatchToProps)(Balance)