import SignUpContainer from './component.js'
import React, { Component } from 'react'
import { connect } from 'react-redux'
import {browserHistory} from 'react-router'
import Lending from 'containers/lending'
import Menu from '../menu'


class SignUp extends Component {

    render(){
     this.props.hideButton ? browserHistory.push('/profile-balance') : null
        return(
            <div>
            <Menu/>
                <br/>
            {/*menu*/}
            <div className='flex-container'>
            {this.props.hideButton ? <Lending /> :  <SignUpContainer />}

            </div>
            </div>
        )
    } 
}

const mapStateToProps = state => ({
   hideButton : state.auth.hideButton
})

export default connect(mapStateToProps, null)(SignUp)

