import R from 'ramda'
import {
    FETCH_COIN_SUCCESS
} from 'actionsTypes'

const initialState = {
    
}

export default (state = initialState, {type , payload}) => {
    switch (type) {
        case FETCH_COIN_SUCCESS :
            const newValueCoin = R.indexBy(R.prop('id'), payload)
            return R.merge(newValueCoin, state)
        default:
            return state
    }
}